mapper.select:

	select 
  		qno, title, content, total, successer, commentary, writer, regdate, updatedate, questionTag
	from (
	    select /*+INDEX_DESC(tbl_Question pk_Question) */
			rownum rn, qno, title, content, total, successer, commentary, writer, regdate, updatedate, questionTag
	    from 
	    	tbl_Question
    	where 



	    rownum <= #{pageNum} * #{amount}
    )
	where rn > (#{pageNum} -1) * #{amount}
